  //   this.setState((state) => ({ name: 'name5', age: state.age + 1 }))
  //   console.log('end')
  // };


  render() {
    console.log('render...', this.state);
    const { name, age } = this.state;
    return (
      <div>
        <span>{name + '---' + age}</span>
        <button id='btn' onClick={this.click}>click me!</button>
      </div>
    )
  }
}

export default App;
