import React, { Component } from 'react'
import './index.css'

export default class Footer extends Component {

    handleAllCheck = (event) => {
        this.props.checkAllTodo(event.target)
    }
    render() {
        const { todos } = this.props
        const completeTodo = todos.reduce((pre, current) => { return pre + (current.done ? 1 : 0) }, 0)

        return (
            <div className='todo-footer'>
                <label>
                    <input type='checkbox' onChange={this.handleAllCheck} checked={completeTodo === todos.length ? true : false} />
                    {/* onChange：取消和勾选时候的回调 */}
                </label>
                <span>
                    <span>Complete {completeTodo} </span> / Total {todos.length}
                </span>
                <button className='btn btn-danger'>Clear the finished tasks</button>
            </div>
        )
    }
}
