import React, { Component } from 'react';
import Header from './components/Header'
import List from './components/List'
import Footer from './components/Footer'
import uuid from 'react-uuid'
import './App.css';
// App.js is normal javascript file which has JSX code inside render function. 
// The name App is user-defined you can keep any name but remember to save as .js file.

// 状态在哪里，操作状态的方法就在哪里

export default class App extends React.Component {
    state = {
        todos: [
            { id: uuid(), name: 'sleep', done: false },
            { id: uuid(), name: 'eat', done: false },
            { id: uuid(), name: 'watch TV', done: false },
        ]
    }

    addTodo = (todoObj) => {
        const {todos} = this.state
        const newTodos = [todoObj, ...todos]
        this.setState({todos: newTodos})
        console.log(this.state)
    }

    updateTodo = (id, done) => {
        const {todos} = this.state
        const newTodos = todos.map((todo) => {
             if(todo.id === id){
                 return {...todo, done}
             } else{
                 return todo
             }
        })
        this.setState({todos: newTodos})
    }

    render() {
        const {todos} = this.state
        return (
            <div className='todo-container'>
                <div className='todo-wrap'>
                    <Header addTodo={this.addTodo}/>
                    <List todos={todos} updateTodo={this.updateTodo}/>
                    <Footer />
                </div>
            </div>
        )
    }

}
